import React, { Component } from 'react';

class ExampleComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      errorMessage: ''
    };
  }

  // Called when the component is mounted to the DOM
  componentDidMount() {
    console.log('Component mounted');
  }

  // Called when the component is updated and passed the previous props and state
  componentDidUpdate(prevProps, prevState) {
    console.log('Component updated');
    if (prevState.count!== this.state.count) {
      console.log('Count state updated');
    }
  }
  componentWillUnmount() {
    console.log('Component will unmount');
  }

  // Called when an error is caught during rendering, in a lifecycle method, or in the constructor of any child component
  static getDerivedStateFromError(error) {
    return { errorMessage: error.toString() };
  }

  // Called when an error is caught during rendering, in a lifecycle method, or in the constructor of any child component
  componentDidCatch(error, errorInfo) {
    console.error('Error caught:', error, errorInfo);
  }

  handleClick = () => {
    if (this.state.count >= 5) {
      throw new Error('Count cannot exceed 5');
    } else {
      this.setState(prevState => ({
        count: prevState.count + 1
      }));
    }
  }

  render() {
    if (this.state.errorMessage) {
      return <h1>{this.state.errorMessage}</h1>;
    }

    return (
      <div>
        <p>Count: {this.state.count}</p>
        <button onClick={this.handleClick}>Increment Count</button>
      </div>
    );
  }
}

export default ExampleComponent;
