import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';

function Football() {
  // State untuk menghitung jumlah tembakan
  const [shots, setShots] = useState(0);

  // Fungsi yang dijalankan ketika tombol ditekan
  const shoot = () => {
    alert("Great Shot!");
    // Menambah jumlah tembakan
    setShots(shots + 1);
  };

  return (
    <div>
      <button onClick={shoot} style={{ padding: '10px 20px', fontSize: '16px', margin: '10px', cursor: 'pointer' }}>Take the shot!</button>
      <h2>Shots Taken: {shots}</h2>
      {/* Menampilkan pesan khusus setelah 5 tembakan */}
      {shots >= 5 && <p style={{ color: 'red' }}>You're on fire!</p>}
    </div>
  );
}

// Mendapatkan elemen root dari HTML
const rootElement = document.getElementById('root');
// Membuat root container jika belum ada
if (rootElement) {
  const root = ReactDOM.createRoot(rootElement);
  root.render(<Football />);
}
