import React, { useState } from 'react';

const App = () => {
  const [guess, setGuess] = useState('');
  const [message, setMessage] = useState('');
  const [randomNumber, setRandomNumber] = useState(Math.floor(Math.random() * 100) + 1);
  const [attempts, setAttempts] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [showVideo, setShowVideo] = useState(false); // State untuk menampilkan video

  const maxAttempts = 6;

  const handleGuessChange = (event) => {
    setGuess(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const userGuess = parseInt(guess);

    if (userGuess === randomNumber) {
      setMessage(`Selamat! Anda menebak angka dengan benar setelah ${attempts + 1} percobaan. \n INI HADIAH MU`);
      setIsGameOver(true);
      setShowVideo(true); // Menampilkan video ketika berhasil menebak
    } else if (userGuess < randomNumber) {
      setMessage('Angka yang Anda masukkan terlalu kecil. Coba lagi.');
    } else {
      setMessage('Angka yang Anda masukkan terlalu besar. Coba lagi.');
    }

    setAttempts(attempts + 1);
    setGuess('');

    if (attempts >= maxAttempts - 1) {
      setMessage(` GAME OVER!!!.. Anda telah menggunakan semua percobaan. Angka yang benar adalah ${randomNumber}.`);
      setIsGameOver(true);
    }
  };

  const handleRestart = () => {
    setRandomNumber(Math.floor(Math.random() * 100) + 1);
    setAttempts(0);
    setIsGameOver(false);
    setMessage('');
    setShowVideo(false); // Reset state untuk menampilkan video
  };

  return (
    <div>
      <h1>Tebak Angka Berhadiah</h1>
      {isGameOver ? (
        <div>
          <p>{message}</p>
          <button onClick={handleRestart}>Main lagi</button>
        </div>
      ) : (
        <div>
          <p>Sisa percobaan: {maxAttempts - attempts}</p>
          <form onSubmit={handleSubmit}>
            <input
              type="number"
              value={guess}
              onChange={handleGuessChange}
              min="1"
              max="100"
              required
            />
            <button type="submit">Tebak</button>
            {message && <p>{message}</p>}
          </form>
        </div>
      )}
      {showVideo && ( // Menampilkan video jika showVideo bernilai true
        <video controls>
          <source src="hadiah.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
      <p><b>SEKALI MAIN BAYAR 2 BITCOIN</b></p>
    </div>
  );
};

export default App;
