import React, { Component } from 'react';

class DOMManipulationExample extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: 'Initial Text'
    };
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    this.setState({ text: 'Text Updated' });
  }
  componentDidMount() {
    // Manipulasi DOM langsung
    const element = document.getElementById('example-div');
    element.style.color = 'red';
  }

  render() {
    return (
      <div>
        <p>{this.state.text}</p>
        <button onClick={this.handleClick}>Update Text</button>
        <div id="example-div">DOM Manipulation Example</div>
      </div>
    );
  }
}

export default DOMManipulationExample;
